import React from 'react';
import { Eye, Brain } from 'lucide-react';
import Accordion from './Accordion';

const ExpertiseSection = () => {
  const cvSections = [
    {
      title: "教育背景",
      content: (
        <ul className="list-disc pl-5">
          <li>○○大学医学部 卒業</li>
          <li>○○大学大学院 医学研究科 修了</li>
        </ul>
      )
    },
    {
      title: "職歴",
      content: (
        <ul className="list-disc pl-5">
          <li>○○病院 眼科医師 (20XX年 - 20XX年)</li>
          <li>○○大学病院 眼形成外科 専門医 (20XX年 - 現在)</li>
        </ul>
      )
    },
    {
      title: "研究経験",
      content: (
        <ul className="list-disc pl-5">
          <li>AI を用いた眼疾患診断システムの開発 (20XX年 - 現在)</li>
          <li>眼瞼下垂の新しい手術technique の開発 (20XX年 - 20XX年)</li>
        </ul>
      )
    },
    {
      title: "出版物",
      content: (
        <ul className="list-disc pl-5">
          <li>"AI-assisted diagnosis of orbital tumors" - Journal of Ophthalmic Research, 20XX</li>
          <li>"Novel approach to blepharoptosis repair" - International Journal of Oculoplastic Surgery, 20XX</li>
        </ul>
      )
    }
  ];

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-4xl font-bold text-center mb-10">My Expertise</h2>
      <div className="grid md:grid-cols-2 gap-8 mb-12">
        <div className="bg-green-50 p-6 rounded-lg shadow-md">
          <div className="flex justify-center mb-4">
            <Eye className="w-16 h-16 text-green-600" />
          </div>
          <h3 className="text-2xl font-semibold text-center mb-4">Oculoplastic Surgeon</h3>
          <p className="text-center mb-6">
            Specialized in reconstructive and aesthetic surgeries of the eye region.
          </p>
        </div>

        <div className="bg-blue-50 p-6 rounded-lg shadow-md">
          <div className="flex justify-center mb-4">
            <Brain className="w-16 h-16 text-blue-600" />
          </div>
          <h3 className="text-2xl font-semibold text-center mb-4">AI Researcher</h3>
          <p className="text-center mb-6">
            Leveraging artificial intelligence to revolutionize ophthalmic diagnostics and treatment.
          </p>
        </div>
      </div>
      <div className="mt-8">
        <h3 className="text-2xl font-bold text-center mb-6">Learn More About Me</h3>
        <Accordion items={cvSections} />
      </div>
    </div>
  );
};

export default ExpertiseSection;
