import React from 'react';
import { Link } from 'react-router-dom';
import { Eye, Brain } from 'lucide-react';

const ExpertiseSection = () => {
  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-4xl font-bold text-center mb-10">My Expertise</h2>
      <div className="grid md:grid-cols-2 gap-8">
        <div className="bg-green-50 p-6 rounded-lg shadow-md">
          <div className="flex justify-center mb-4">
            <Eye className="w-16 h-16 text-green-600" />
          </div>
          <h3 className="text-2xl font-semibold text-center mb-4">Oculoplastic Surgeon</h3>
          <p className="text-center mb-6">
            Specialized in reconstructive and aesthetic surgeries of the eye region.
          </p>
          {/* Add more content here */}
        </div>

        <div className="bg-blue-50 p-6 rounded-lg shadow-md">
          <div className="flex justify-center mb-4">
            <Brain className="w-16 h-16 text-blue-600" />
          </div>
          <h3 className="text-2xl font-semibold text-center mb-4">AI Researcher</h3>
          <p className="text-center mb-6">
            Leveraging artificial intelligence to revolutionize ophthalmic diagnostics and treatment.
          </p>
          {/* Add more content here */}
        </div>
      </div>
      <div className="mt-8 text-center">
        <Link to="/cv" className="bg-purple-600 text-white px-6 py-2 rounded-full hover:bg-purple-700 transition-colors">
          Learn More About Me
        </Link>
      </div>
    </div>
  );
};

export default ExpertiseSection;
